// Exibir mensagem padronizada
function montarMensagem($_tipo, $_mensagem, $_complemento = '',$_categoria = ''){
    $_exibirMensagem = '<div id="mensagem" class="container alert alert-'.$_tipo.' alert-dismissible fade show" role="alert">';
    foreach($_mensagem as $_msg){
        $_exibirMensagem .= (($_tipo == "danger") ? "" : "").$_msg."</br>"; 
        if ($_tipo == "danger") {
            gravaXTrace("Erro: ".$_msg);
        }
    }
    $_exibirMensagem .= '<button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button></div>';

    $constraint = (strpos($_exibirMensagem,'CONSTRAINT') !== false ? true : false);
    if ($_categoria != '' && !$constraint) {
        $_SESSION['exibirMensagem'.$_categoria] = $_exibirMensagem;
    } else {
        $_SESSION['exibirMensagem'] = $_exibirMensagem;
    }
    if ($_tipo == "danger" && !empty($_complemento)) {
        gravaXTrace($_complemento);
    }
}

function exibirMensagem($_categoria = ''){
    if (isset($_SESSION['exibirMensagem'.$_categoria]) && $_SESSION['exibirMensagem'.$_categoria] != "") {
        echo $_SESSION['exibirMensagem'.$_categoria];
        $_SESSION['exibirMensagem'.$_categoria] = "";
    }
}

function verificaRequest($_evento = "", $_restricoes = array("salvar","excluir")) {
    $_retorno = true;
    if (($_evento == "") || (in_array($_evento, $_restricoes))) { 
        $hash = md5( implode($_POST) );
        if(isset($_SESSION['hash'] ) && $_SESSION['hash'] == $hash ) { 
            montarMensagem("warning",array('Tentativa de repetir a mesma operação!')); 
            $_retorno = false; 
        } else { $_SESSION['hash'] = $hash; }   
