    return $_retorno;
}

function prepararLog($_arquivo) {
    $_retorno = 'danger';
    if (file_exists($_arquivo)) {
        $_dados = file($_arquivo);
        foreach($_dados as $_linha){
            $_linha = trim($_linha);
            if (!empty($_linha)) {
                $linha = explode(' <> ', $_linha);
                $_retorno = (count($linha) >= 1 ? $linha[0] : 'success');
                $_mensagens[] = (count($linha) >= 2 ? $linha[1] : '');
            }
        }
        $_retorno = true;
    }
    return $_retorno;
}

