// Testa se campos estão preenchidos
function camposPreenchidos($_args, $_labels = array(), $_permiteZero = true){
    $_erros = array();
    if(!empty($_args) && is_array($_args)){
        if (empty($_labels)) { $_labels = $_args; }
        $_cpo = -1;
        $_lbl = -1;
        foreach($_args as $_arg){
            ++$_lbl;
            $_campo = $_arg;
            $_campo = $_POST[$_arg];
            if (($_permiteZero && isset($_campo) && $_campo !== "") || 
                (!$_permiteZero && !empty($_campo) && $_campo !== "0" && $_campo !== 0)) {
            } else {
                $_erros[++$_cpo] = "Campo [".$_labels[$_lbl]."] deve ser informado.";
            }
        }
    }
    return $_erros;
}


