// Excluir arquivo
function excluirArquivo($arquivo){
    // faz o teste se a variavel não esta vazia e se o arquivo realmente existe
    if(isset($arquivo) && file_exists($arquivo)){
        // exclui arquivo
        unlink($arquivo); 
    }   
}

// Listar os arquivos de uma pasta
function listarArquivos($_path, $_extensao = "*", $_ordem = 0) {
    $_pastas = array();
    $_diretorio = scandir($_path, $_ordem);
    if (count($_diretorio) > 0){
        foreach ($_diretorio as $_arquivo) {
            if ($_extensao == "*" || strpos($_arquivo, '.'.$_extensao)) {
                $_pastas[] = ["nome"=>$_path.$_arquivo]; 
            }
        }
    }
    return $_pastas;
}

// Apagar os arquivos de uma pasta
function apagarArquivos($_path, $_extensao = "*") {
    $_pasta = array();
    $_diretorio = dir($_path); 
    while($_arquivo = $_diretorio -> read()){
        if ($_extensao == "*" || strpos($_arquivo, '.'.$_extensao)) {
            unlink($_path.$_arquivo);
        }
    }
    $_diretorio -> close();
}

