// Leitura do Log de Processos adequando para o montarMensagem
function lerXLogProcesso($_arquivo) {
    if (file_exists($_arquivo)) {
        $_log = file($_arquivo);
        $_eliminar = trim($_log[0]);
        $_tipoMsg = (strpos($_eliminar,'success') ? 'success' : (strpos($_eliminar,'warning') ? 'warning' : 'danger'));
        foreach($_log as $_linha){
            $_mensagens[] = str_replace($_eliminar,"", $_linha);
        }
        return array($_tipoMsg, $_mensagens);
    } else {
        return array("danger", array("Arquivo log não foi gerado"));
    } 
}

