function executaAPIs($_php,$_post) {
    // Monta a URL 
    if ($_SERVER["HTTP_HOST"] == "localhost") {
        $_url = 'http://localhost/gear/api/'.$_php;
    } else {
        $_url = 'https://gear.encodeinformatica.com.br/api/'.$_php;
    }
    $_api = curl_init();
    curl_setopt_array($_api, [
        CURLOPT_URL => $_url,
        CURLOPT_RETURNTRANSFER => true,             // Retornar string
        CURLOPT_POST => true,                       // Método do request
        CURLOPT_POSTFIELDS => json_encode($_post)   // Dados
    ]);
    $_apiRetorno = curl_exec($_api);
    $_httpCode = curl_getinfo($_api, CURLINFO_HTTP_CODE);
    curl_close($_api);

    if ($_apiRetorno === false) {
        $_retorno = ['status' => 'ERRO', 'msg' => 'Erro '.curl_error($_api).' na chamada da api '.$_php ];
    } else if ($_httpCode >= 400 && $_httpCode < 600) {
        $_retorno = ['status' => 'ERRO', 'msg' => 'Erro HTTP '.$_httpCode.' na api '.$_php ];
    } else {
        $_retorno = preg_replace('/[\x00-\x1f\x80-\xff]/', '', $_apiRetorno);
    // var_dump("String original (hex): " . bin2hex($_apiRetorno));
    // var_dump("String limpa (hex): " . bin2hex($_retorno));
        $_retorno = json_decode($_retorno, true);
        if (is_null($_retorno)) {
            $_retorno = ['status' => 'ERRO', 'msg' => 'Erro api '.$_php.' ('.json_last_error_msg().') retornou nulo - '.$_apiRetorno];
        } elseif (!is_array($_retorno)) {
            $_retorno = ['status' => 'ERRO', 'msg' => 'Erro api '.$_php.' ('.json_last_error_msg().') não retornou um array - '.$_retorno];
        } elseif (!isset($_retorno['status'])) {
            $_retorno = ['status' => 'ERRO', 'msg' => 'Erro api '.$_php.' não retornou a chave status - '.$_apiRetorno];
        } elseif (json_last_error() !== JSON_ERROR_NONE) {
            $_retorno = ['status' => 'ERRO', 'msg' => 'Erro api '.$_php.' erro na decodificao do json - '.$_apiRetorno];        
        }
    }
    return $_retorno;
}

function gerarToken($_usuario){
    // O JWT é divido em três partes separadas por ponto ".": um header, um payload e uma signature
    // Header indica o tipo do token "JWT", e o algoritmo utilizado "HS256"
    $header = ['alg' => 'HS256','typ' => 'JWT'];
    $header = json_encode($header);
    $header = base64_encode($header);

    // O payload é o corpo do JWT, recebe as informações que precisa armazenar
    // iss - O domínio da aplicação que gera o token
    // aud - Define o domínio que pode usar o token
    // exp - Data de vencimento do token
    // 7 days; 24 hours; 60 mins; 300secs
    // Duração de 1440 segundos
    $payload = ['exp' => time() + 600,'cliente' => $_usuario];
    $payload = json_encode($payload);
    $payload = base64_encode($payload);

    // O signature é a assinatura. Pegar o header e o payload e codificar com o algoritmo sha256, junto com a chave
    $chave = "81f705dc2ce1a61a2621e0e4b442a9474e1d0c70";
    
    // Gera um valor de hash com a chave usando o método HMAC
    $signature = hash_hmac('sha256', "$header.$payload", $chave, true);
    $signature = base64_encode($signature);

    // Retornar o token
    return "$header.$payload.$signature";
}

function validarToken($_token){
    // Token pode ser validado no site https://jwt.io
    // Converter o token em array
    // Para testes utilizar o token "__GEAR__"
    if ($_token != "__GEAR__") {
        try {
            $token_array = explode('.', $_token);
            $header = (isset($token_array[0]) ? $token_array[0] : "");
            $payload = (isset($token_array[1]) ? $token_array[1] : "");
            $signature = (isset($token_array[2]) ? $token_array[2] : "");
            
            // Chave usada para gerar o token a mesma deve ser usada para validar o token
            $chave = "81f705dc2ce1a61a2621e0e4b442a9474e1d0c70";
            
            // Usar o header e o payload e codificar com o algoritmo sha256
            $validar_assinatura = hash_hmac('sha256', "$header.$payload", $chave, true);
            $validar_assinatura = base64_encode($validar_assinatura);
            
            // Comparar a assinatura do token recebido com a assinatura gerada.
            $_retorno = false;
            if ($signature == $validar_assinatura) {
                $dados_token = base64_decode($payload);
                $dados_token = json_decode($dados_token);
                // Comparar a data de vencimento do token com a data atual
                $_retorno = ($dados_token->exp > time());
            }
        } catch (Exception $e) {
            $_retorno = false;
        }
    } else {
        $_retorno = true;
    }
    return $_retorno;
}

// Verifica o número de conexões
